DROP TABLE DEV.SAMPLE_BOARD PURGE;

CREATE TABLE DEV.SAMPLE_BOARD (
  "ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE
  , "TITLE" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "CONTENTS" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "AUTHOR" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "CREATE_USER" VARCHAR2(8) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "CREATE_TIME" TIMESTAMP (0) DEFAULT NULL
  , "UPDATE_USER" VARCHAR2(8) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "UPDATE_TIME" TIMESTAMP (0) DEFAULT NULL
  , PRIMARY KEY ("ID")
  ) TABLESPACE "SAMPLESCHEMA" NOLOGGING;

COMMENT ON TABLE DEV.SAMPLE_BOARD IS 'SAMPLE 게시판';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.ID IS 'ID';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.TITLE IS '게시글 제목';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.CONTENTS IS '게시글 내용';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.AUTHOR IS '작성자';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.CREATE_USER IS '최초 등록 사용자';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.CREATE_TIME IS '최초 등록 시간';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.UPDATE_USER IS '최종 수정 사용자';
COMMENT ON COLUMN DEV.SAMPLE_BOARD.UPDATE_TIME IS '최종 수정 시간';

SELECT 'ALTER INDEX ' || A.INDEX_NAME || ' RENAME TO SAMPLE_BOARD_X01;'
FROM ALL_IND_COLUMNS A
  , ALL_INDEXES B
WHERE A.INDEX_NAME = B.INDEX_NAME
  AND A.TABLE_NAME = 'SAMPLE_BOARD'
  AND A.COLUMN_NAME = 'ID';
