DROP TABLE DEV.USER_LOG_LOG PURGE;

CREATE TABLE DEV.USER_LOG_LOG (
  "ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE
  , "USER_ID" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "USER_PW" VARCHAR2(64) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "USER_NAME" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "CREATE_USER" VARCHAR2(8) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "CREATE_TIME" TIMESTAMP (0) DEFAULT NULL
  , "UPDATE_USER" VARCHAR2(8) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "UPDATE_TIME" TIMESTAMP (0) DEFAULT NULL
  , PRIMARY KEY ("ID")
  ) TABLESPACE "SAMPLESCHEMA" NOLOGGING;

COMMENT ON TABLE DEV.USER_LOG IS '사용자';
COMMENT ON COLUMN DEV.USER_LOG.ID IS 'ID';
COMMENT ON COLUMN DEV.USER_LOG.USER_ID IS '사용자 ID';
COMMENT ON COLUMN DEV.USER_LOG.USER_PW IS '사용자 패스워드';
COMMENT ON COLUMN DEV.USER_LOG.USER_NAME IS '사용자 이름';
COMMENT ON COLUMN DEV.USER_LOG.CREATE_USER IS '최초 등록 사용자';
COMMENT ON COLUMN DEV.USER_LOG.CREATE_TIME IS '최초 등록 시간';
COMMENT ON COLUMN DEV.USER_LOG.UPDATE_USER IS '최종 수정 사용자';
COMMENT ON COLUMN DEV.USER_LOG.UPDATE_TIME IS '최종 수정 시간';

SELECT 'ALTER INDEX ' || A.INDEX_NAME || ' RENAME TO USER_LOG_X01;'
FROM ALL_IND_COLUMNS A
  , ALL_INDEXES B
WHERE A.INDEX_NAME = B.INDEX_NAME
  AND A.TABLE_NAME = 'USER_LOG'
  AND A.COLUMN_NAME = 'ID';
